package kr.or.yi.java_study_01.ch04;

public class Rectancle {
	int width;
	int height;
	
	public Rectancle() {
		// TODO Auto-generated constructor stub
	}

	public Rectancle(int width, int height) {
		this.width = width;
		this.height = height;
	}



	public int getArea() {
		return width * height;
		
	}
}
